SET NAMES utf8;

DROP DATABASE IF EXISTS bibliotheque;
CREATE DATABASE bibliotheque CHARACTER SET 'utf8';

USE bibliotheque;

CREATE TABLE auteur(
        idAuteur        Int  NOT NULL AUTO_INCREMENT,
        nom             Varchar (80) NOT NULL ,
        bibliographie   Text,
	CONSTRAINT auteur_PK PRIMARY KEY (idAuteur)
)ENGINE=InnoDB;

CREATE TABLE editeur
(
   idEditeur            int not null AUTO_INCREMENT,
   nom                  varchar(80) NOT NULL,
   lien_description     varchar(200),
   CONSTRAINT editeur_PK PRIMARY KEY (idEditeur)
)ENGINE=InnoDB;

CREATE TABLE livres (
   id int NOT NULL AUTO_INCREMENT,
   titre varchar(150) NOT NULL,
   nb int NOT NULL,
   nbPages int NOT NULL,
   image varchar(150) NOT NULL,
   description text NOT NULL,
   idAuteur    Int NOT NULL ,
   idEditeur    Int NOT NULL ,
   CONSTRAINT livre_PK PRIMARY KEY (id) ,
   CONSTRAINT livre_auteur_FK FOREIGN KEY (idAuteur) REFERENCES auteur(idAuteur) ON DELETE CASCADE ,
   CONSTRAINT livre_editeur_FK FOREIGN KEY (idEditeur) REFERENCES editeur(idEditeur) ON DELETE CASCADE
)ENGINE=InnoDB;

CREATE TABLE IF NOT EXISTS  utilisateur (
  login varchar(50) NOT NULL,
  password varchar(100) NOT NULL,
  mail varchar(100) NOT NULL,
  role varchar(50) NOT NULL,
  image varchar(100),
  est_valide tinyint(1) NOT NULL,
  clef varchar(50), 
  PRIMARY KEY (login)
) ENGINE=InnoDB;

CREATE TABLE utilisateur_livre_emprunter(
        idEmprunt Int  Auto_increment  NOT NULL ,
        idLivre   Int NOT NULL ,
        login     Varchar (50) NOT NULL ,
        date_pret DateTime NOT NULL , 
        date_retour DateTime ,
	CONSTRAINT utilisateur_livre_emprunter_PK PRIMARY KEY (idEmprunt) ,
	CONSTRAINT utilisateur_livre_emprunter_livre_FK FOREIGN KEY (idLivre) REFERENCES livres(id) ,
	CONSTRAINT utilisateur_livre_emprunter_utilisateur0_FK FOREIGN KEY (login) REFERENCES utilisateur(login)
)ENGINE=InnoDB;

START TRANSACTION;
;

